def get_trees_for_n(df, n):
    """Get trees for a specific N"""
    prefix = f"{n:03d}_"
    return df[df['id'].str.startswith(prefix)].copy()

def compute_side_for_trees(trees_df):
    """Compute bounding box side for a set of trees"""
    polygons = []
    for _, row in trees_df.iterrows():
        x = parse_value(row['x'])
        y = parse_value(row['y'])
        deg = parse_value(row['deg'])
        polygons.append(create_tree_polygon(x, y, deg))
    return compute_bounding_side(polygons)

# Build ensemble
ensemble_rows = []
total_ensemble_score = 0

for n in range(1, 201):
    best_side = float('inf')
    best_trees = None
    best_source = None

    for src, df in dfs.items():
        trees = get_trees_for_n(df, n)
        if len(trees) == n:
            side = compute_side_for_trees(trees)
            if side < best_side:
                best_side = side
                best_trees = trees
                best_source = src

    if best_trees is not None:
        ensemble_rows.append(best_trees)
        score_n = best_side**2 / n
